CREATE OR REPLACE FUNCTION split2 (p_clob IN CLOB, p_delim IN VARCHAR2 := '/')
   RETURN DBMS_SQL.varchar2a
IS
   l_current_pos      NUMBER;
   l_next_delim_pos   NUMBER;
   l_read_amount      NUMBER;
   oret               DBMS_SQL.varchar2a;
   l_clob             CLOB;
BEGIN
   IF p_clob IS NULL
   THEN
      RETURN oret;
   END IF;

   IF DBMS_LOB.getlength (p_clob) = 0
   THEN
      RETURN oret;
   END IF;

   l_current_pos := 1;

   DBMS_OUTPUT.put_line ('lob length: ' || DBMS_LOB.getlength (p_clob));

   WHILE l_current_pos <= DBMS_LOB.getlength (p_clob)
   LOOP
      -- find the position of the next delimiter
      l_next_delim_pos := DBMS_LOB.INSTR (p_clob, p_delim, l_current_pos);
      DBMS_OUTPUT.put_line ('delim_pos: ' || l_next_delim_pos);
      l_clob := '';

      -- is the next delimiter within the next 32k?

      IF (l_next_delim_pos > 0)
      THEN
         l_read_amount := l_next_delim_pos - l_current_pos;

         IF (l_read_amount < 32000)
         THEN
            DBMS_OUTPUT.put_line (
                  'reading from: '
               || l_current_pos
               || ' to: '
               || (l_current_pos + l_read_amount));
            l_clob := DBMS_LOB.SUBSTR (p_clob, l_read_amount, l_current_pos);

            INSERT INTO clob_dump (id, data)
                 VALUES (clob_dump_seq.NEXTVAL, l_clob);

            oret (oret.COUNT + 1) := l_clob;

            l_current_pos := l_current_pos + l_read_amount + 1;
         ELSE
            DBMS_OUTPUT.put_line ('l_read_amount: ' || l_read_amount);
            l_clob := DBMS_LOB.SUBSTR (p_clob, 32000, l_current_pos);

            INSERT INTO clob_dump (id, data)
                 VALUES (clob_dump_seq.NEXTVAL, l_clob);

            oret (oret.COUNT + 1) := l_clob;

            DBMS_OUTPUT.put_line (
                  'reading from: '
               || l_current_pos
               || ' to: '
               || (l_current_pos + 32000));
            l_current_pos := l_current_pos + 32000;
         END IF;
      ELSE
         -- should never get here
         DBMS_OUTPUT.put_line ('should never get here!');
         l_current_pos := l_current_pos + 1;
      END IF;
   END LOOP;

   RETURN oret;
EXCEPTION
   WHEN OTHERS
   THEN
      DBMS_OUTPUT.put_line ('oh crap: ' || SQLERRM);
END split2;
/

DECLARE
   ostr     DBMS_SQL.varchar2a;
   l_data   CLOB;
BEGIN
   SELECT data INTO l_data FROM dummy_data;

   ostr := split2 (l_data, '/');

   FOR indx IN 1 .. ostr.COUNT
   LOOP
      DBMS_OUTPUT.put_line (ostr (indx));
   END LOOP;

   DBMS_OUTPUT.put_line ('Count=' || ostr.COUNT);
END;
